mapper.insert:
insert into hotel
values(
	SEQ_HID.nextval, #{entId}, #{hotelName}, #{hotelPhone}, #{hotelAddress}, #{wifi}, #{breakfast}, #{amenity}, #{park}, #{swim}, #{fitness}, default, default, #{star}, #{hotelInfo}, #{hotelIntro}, default, #{hotelGeoX}, #{hotelGeoY}
)

mapper.select:
select count(*)
from like_hotel
where id=#{id} and hotel_id=#{hotelId}

select hotel_id, ent_id, hotel_name, hotel_phone, hotel_address, wifi, breakfast, amenity, park, swim, fitness, ent_approval, modify_date, star, hotel_info, hotel_intro, hotel_status, hotel_geo_x, hotel_geo_y, min_price(hotel_id) as min_price, avg_rating(hotel_id) as avg_rating, empty_hotel(#{checkinDate}, #{checkoutDate}, hotel_id) as empty_hotel
, DISTNACE_WGS84(round(hotel_geo_x, 10), round(hotel_geo_y, 10), 126.950305407863, 37.5632043013128) as max_distance
from hotel where hotel_status='Y'
and min_price(hotel_id)<#{maxPrice}
and DISTNACE_WGS84(round(hotel_geo_x, 10), round(hotel_geo_y, 10), 126.950305407863, 37.5632043013128)<#{maxDistance}
order by hotel_id desc
