mapper.select:
select BOARD_ID, BOARD_WRITER, BOARD_COURSE, BOARD_TITLE, BOARD_CONTENT, BOARD_CREATE_DATE, CONTENT_COURSE
from board
where board_id = #{boardId}


    select FILE_MODIFY_NAME
	from ATTACHED_FILE join board_img using (FILE_ID)
	where board_id = #{ boardId }

select count(*)
from Reply
where REPLY_STATUS = 'Y' and REF_BOARD_ID = #{boardId}

mapper.update:
update board
set board_status='N'
where board_id=#{boardId}
